// Wierd things about Big Numbers

console.log(Number.MAX_VALUE);

console.log(Number.MAX_VALUE + 1);

console.log(
  Number.MAX_VALUE +
    999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999
);
console.log(
  Number.MAX_VALUE +
    9999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999991
);

// The Number.MAX_SAFE_INTEGER constant represents the maximum safe integer in JavaScript (2⁵³-1).
console.log(Number.MAX_SAFE_INTEGER);

console.log(Number.MAX_SAFE_INTEGER + 1);

let a = Number.MAX_SAFE_INTEGER + 1;
let b = Number.MAX_SAFE_INTEGER + 2;
console.log(a === b);

// Go beyond max safe integer
// no support on floating point numbers and old browsers incompatible
const theBiggestInt = 9007199254740991n;
console.log(typeof theBiggestInt);

console.log(BigInt(Number.MAX_VALUE));
// 

console.log(BigInt(Number.MAX_VALUE + 1));

console.log(BigInt(Number.MAX_VALUE) + 1n);
/*

*/

let ab = BigInt(Number.MAX_VALUE);
let bb = BigInt(Number.MAX_VALUE) + 1n;
console.log(ab === bb);
